import React, { Component } from "react";
import styled from "styled-components";
import Header from "./header";
import Card from "../repeated/customCard";
import Img from "../../static/img";

const Wrapper = styled.div`
  width: 100%;
  padding: 10px;
`;

const Title = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  > h1 {
    font-size: 20px;
    font-weight: bolder;
    color: blue;
  }
`;

const CardContainer = styled.div`
  width: 100%;
  margin-top: 50px;
  display: flex;
  flex-flow: row wrap;
`;

const Container = styled.div`
  display: flex;
  flex-direction: row;

  /* padding: 38px 48px; */
`;
const SearchBar = styled.input`
  font-size: 18px;
  /* margin: 20px 0px; */
  margin-left: 5% !important;
  width: 65%;
  height: 100%;
  padding: 14px 0px;

  font-size: 15px;
  border: 1px solid #eee;
  :focus {
    border: none;
  }
  ::placeholder {
    color: #999999;
  }
  @media only screen and (max-width: 1440px) {
    /* margin: 15px 0px; */
    padding: 15px 0px;
    font-size: 16px;
    letter-spacing: -0.3px;
    /* width: 16px;     */
  }
  @media only screen and (max-width: 992px) {
    /* margin: 0px 30px; */
    padding: 14px 0px;
    width: 80%;
    margin-left: 8% !important;
  }
`;
const SearchButton = styled.button`
  /* background-image: linear-gradient(127deg, #ff632a, #e20000); */
  width: 11%;
  border: none;
  border-radius: 0px 3px 3px 0px;
  display: flex;
  align-items: center;
  justify-content: center;
  outline: none;
  cursor: pointer;

  background: ${props =>
    `linear-gradient(127deg, ${props.theme.primary}, ${props.theme.secondary})`};
  > img {
    width: 50%;
  }
  @media only screen and (max-width: 992px) {
    width: 20%;
  }
`;

const Data = [
  {
    id: 1,
    email: "george.bluth@reqres.in",
    first_name: "George",
    last_name: "Bluth",
    avatar: "https://s3.amazonaws.com/uifaces/faces/twitter/calebogden/128.jpg"
  },
  {
    id: 2,
    email: "janet.weaver@reqres.in",
    first_name: "Janet",
    last_name: "Weaver",
    avatar: "https://s3.amazonaws.com/uifaces/faces/twitter/josephstein/128.jpg"
  },
  {
    id: 3,
    email: "emma.wong@reqres.in",
    first_name: "Emma",
    last_name: "Wong",
    avatar:
      "https://s3.amazonaws.com/uifaces/faces/twitter/olegpogodaev/128.jpg"
  },
  {
    id: 4,
    email: "eve.holt@reqres.in",
    first_name: "Eve",
    last_name: "Holt",
    avatar:
      "https://s3.amazonaws.com/uifaces/faces/twitter/marcoramires/128.jpg"
  },
  {
    id: 5,
    email: "charles.morris@reqres.in",
    first_name: "Charles",
    last_name: "Morris",
    avatar: "https://s3.amazonaws.com/uifaces/faces/twitter/stephenmoon/128.jpg"
  },
  {
    id: 6,
    email: "tracey.ramos@reqres.in",
    first_name: "Tracey",
    last_name: "Ramos",
    avatar: "https://s3.amazonaws.com/uifaces/faces/twitter/bigmancho/128.jpg"
  }
];

class HomePage extends Component {
  state = -{
    term: ""
  };
  render() {
    const { term } = this.state;
    return (
      <Wrapper>
        <Header />
        <Title>
          <h1>HomePage</h1>
        </Title>
        <Container>
          <SearchBar
            type="text"
            placeholder={"Search Firstname, lasname or email"}
            value={term}
            onChange={this.handleInputChange}
            // onFocus={() => this.setSuggestion()}
            // onBlur={() =>
            //   this.setState({ showSuggestion: false, hovered: null })
            // }
            onKeyDown={this.onUpDown}
          />
          <SearchButton onClick={() => this.onChange()}>
            <img src={Img.Search} />
          </SearchButton>
        </Container>

        <CardContainer>
          {Data.map((item, index) => (
            <Card data={item} />
          ))}
        </CardContainer>
      </Wrapper>
    );
  }
}

export default HomePage;
