import React, { Component } from "react";
import styled from "styled-components";
import Header from "./header";
import Card from "../repeated/customCard";
import Img from "../../static/img";
import Search from "./search";

const Wrapper = styled.div`
  width: 100%;
  padding: 10px;
`;

const Title = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  > h1 {
    font-size: 20px;
    font-weight: bolder;
    color: blue;
  }
`;

const CardContainer = styled.div`
  width: 100%;
  margin-top: 50px;
  display: flex;
  flex-flow: row wrap;
`;

const Container = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;

  /* padding: 38px 48px; */
`;
const SearchBar = styled.input`
  font-size: 18px;
  width: 45%;
  height: 16%;
  padding: 14px 0px;
  font-size: 15px;
  border: 1px solid #000;
  :focus {
    border: none;
  }
  ::placeholder {
    color: #999999;
  }
`;
const SearchButton = styled.button`
  /* background-image: linear-gradient(127deg, #ff632a, #e20000); */
  width: 5%;
  /* height: 8%; */
  border-radius: 0px 5px 5px 0px;
  border: none;
  /* box-shadow: 0px 3px 3px 0px; */
  display: flex;
  align-items: center;
  justify-content: center;
  outline: none;
  cursor: pointer;
  background: linear-gradient(to right, #00ff00 0%, #00ffff 100%);
  > img {
    width: 50%;
  }
  @media only screen and (max-width: 992px) {
    width: 20%;
  }
`;

const Data = [
  {
    id: 1,
    email: "george.bluth@reqres.in",
    first_name: "George",
    last_name: "Bluth",
    avatar: "https://s3.amazonaws.com/uifaces/faces/twitter/calebogden/128.jpg"
  },
  {
    id: 2,
    email: "janet.weaver@reqres.in",
    first_name: "Janet",
    last_name: "Weaver",
    avatar: "https://s3.amazonaws.com/uifaces/faces/twitter/josephstein/128.jpg"
  },
  {
    id: 3,
    email: "emma.wong@reqres.in",
    first_name: "Emma",
    last_name: "Wong",
    avatar:
      "https://s3.amazonaws.com/uifaces/faces/twitter/olegpogodaev/128.jpg"
  },
  {
    id: 4,
    email: "eve.holt@reqres.in",
    first_name: "Eve",
    last_name: "Holt",
    avatar:
      "https://s3.amazonaws.com/uifaces/faces/twitter/marcoramires/128.jpg"
  },
  {
    id: 5,
    email: "charles.morris@reqres.in",
    first_name: "Charles",
    last_name: "Morris",
    avatar: "https://s3.amazonaws.com/uifaces/faces/twitter/stephenmoon/128.jpg"
  },
  {
    id: 6,
    email: "tracey.ramos@reqres.in",
    first_name: "Tracey",
    last_name: "Ramos",
    avatar: "https://s3.amazonaws.com/uifaces/faces/twitter/bigmancho/128.jpg"
  }
];

class HomePage extends Component {
  state = -{
    term: ""
  };

  handleInputChange = e => {
    this.setState({
      term: e
    });
  };

  render() {
    const { term } = this.state;
    return (
      <Wrapper>
        <Header />
        <Title>
          <h1>HomePage</h1>
        </Title>
        <Container>
          {/* <SearchBar
            type="text"
            placeholder={"Search Firstname, lasname or email"}
            value={term}
            onChange={this.handleInputChange}
            // onFocus={() => this.setSuggestion()}
            // onBlur={() =>
            //   this.setState({ showSuggestion: false, hovered: null })
            // }
            onKeyDown={this.onUpDown}
          /> */}
          <Search suggestions={Data} />
          <SearchButton onClick={() => this.onChange()}>
            <img src={Img.Search} />
          </SearchButton>
        </Container>

        <CardContainer>
          {Data.map((item, index) => (
            <Card data={item} />
          ))}
        </CardContainer>
      </Wrapper>
    );
  }
}

export default HomePage;
